function cal(a,operation , b) {
  switch (operation) {
    case '+':
      console.log(a + '+' + b + '=', a + b);
      return a + b;
    
    case '-':
      console.log(a + '-' + b + '=', a - b);
      return a - b;
    
    case '*':
      console.log(a + '*' + b + '=', a * b);
      return a * b;
      
    case '/':
      console.log(a + '/' + b + '=', a / b);
      return a / b;
      
    case '%':
      console.log(a + '%' + b + '=', a % b);
      return a % b;
      
    default:
      console.log('Check your operation');
      return NaN; // Not a Number
  }
}

module.exports = {
  cal
}
